Ext.define('MyApp.view.ChartWindow', {
	extend: 'Ext.window.Window',

	height: 431,
	width: 615,
	layout: {
		type: 'fit'
	},
	title: 'График тарировочной таблицы',
	maximizable: true,
	modal: true,

	initComponent: function() {
		var me = this;

		Ext.applyIf(me, {
			items: [
				{
					xtype: 'chart',
					height: 250,
					width: 400,
					animate: true,
					insetPadding: 20,
					store: 'LLSStore',
					axes: [
						{
							type: 'Numeric',
							fields: [
								'fuel_value'
							],
							title: 'Уровень топлива',
							position: 'left'
						},
						{
							type: 'Numeric',
							fields: [
								'sensor_value'
							],
							position: 'bottom',
							title: 'Показания датчика'
						}
					],
					series: [
						{
							type: 'line',
							xField: 'sensor_value',
							yField: 'fuel_value',
							smooth: 3
						}
					]
				}
			]
		});

		me.callParent(arguments);
	}

});